<?phpnamespace App\Models;use Illuminate\Notifications\DatabaseNotification;class Notification extends DatabaseNotification {    /**     * Get the entity's notifications.     *     * @return \Illuminate\Database\Eloquent\Relations\MorphMany     */    public function user()    {        return $this->morphMany(DatabaseNotification::class, 'notifiable')->orderBy('created_at', 'desc');    }}