<?phpif (! function_exists('user_avatar')) {    /**     * @param  \App\Models\User|int|null  $user     * @return string     */    function user_avatar(\App\Models\User|int|null $user): string {        if (is_int($user)) $user = \App\Models\User::find($user);        if (!$user) $user = Auth::user();        if (!$user) return "";        return $user->photo ?            '<img data-src="'.asset($user->photo).'" alt="" class="lozad">' :            "<span>{$user->short_name}</span>";    }}